import React, { Fragment } from 'react';
import './App.css';
import Main from './components/Main'
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import CharacterContainer from './containers/characterContainer';
import SpellsContainer from './containers/spellsContainer';
import Hat from './components/Hat'
import Welcome from './components/Welcome'
import Login from './components/Login'
import SignUp from './components/SignUp'

class App extends React.Component{
  constructor(){
    super()
    this.state={
      characters:[],
      spells:[],
      currentUser: null,
      users:[],
      logInUser: null,
      validName: null
    }
  }

  setCurrentUser = (e) =>{

    e.preventDefault()

    let findUser = this.state.users.find(user => user.name === this.state.logInUser)

    if(findUser !== undefined){
      this.setState({
        validName:true
      })
    }else{
      window.alert("Wrong username or password")
    }
  }

  setLogInUser = (e) => {

    this.setState({
      logInUser: e.currentTarget.value

    })
  }

  signUp = (e) => {

    e.preventDefault()
    debugger
    if(this.state.users.find(user => user.name === this.state.logInUser) === undefined){
      }
    this.setState({
      currentUser:true
    })

  }

  componentDidMount(){
    fetch("http://localhost:3000/characters")
    .then(res=>res.json())
    .then(characters=> {
      this.setState({
        characters
      })
    })

    fetch("http://localhost:3000/spells")
    .then(res => res.json())
    .then(spells=>{
      this.setState({
        spells
      })
    })

    fetch("http://localhost:3000/users")
    .then(res => res.json())
    .then(users=>{
      this.setState({
        users
      })
    })

  }
  render(){

  return (
    <Fragment>

  <Router>
    <Route exact path='/'           component={Welcome}/>
    <Route exact path='/hat'        render={()=> this.state.validName   ? <Hat/> : <Redirect to ='signup'/>}/>
    <Route exact path='/main'       render={()=> this.state.currentUser ? <Main /> : <Redirect to ="/login"/>}/> 
    <Route exact path='/login'      render={()=> this.state.currentUser ? <Redirect to ='/main'/> : <Login setCurrentUser={this.setCurrentUser} setLogInUser={this.setLogInUser}/>} /> 
    <Route exact path='/signup'     render={()=> this.state.currentUser ? <Redirect to ='/main'/> : <SignUp setLogInUser={this.setLogInUser} user={this.state.logInUser}/> } />
    <Route exact path='/characters' render={()=> this.state.currentUser ? <CharacterContainer characters={this.state.characters} /> :  <Redirect to ="/login"/>} /> 
    <Route exact path='/spells'     render={()=> this.state.currentUser ? <SpellsContainer spells={this.state.spells} /> : <Redirect to ="/login"/>} />
  </Router>
  </Fragment>
  )};
}

export default App;
